import React, {useState, useEffect} from 'react';
import CryptoList from './CryptoList';


function WatchList(props) {

    const [watchedCoins, setWatchedCoins] = useState([]);
    const [updatedWatchList, setUpdatedWatchList] = useState([]);

    useEffect(() => {
        fetch("http://localhost:3000/watchList")
        .then(r=>r.json())
        .then(data=> {
            console.log(data)
            // I'd say put a debugger; here, then open your dev tools and check it in a browser.
            // data show be some kind of JSON object.

            //console.log here to see what data looks like
            // check out key/value pairs of the object and find what you want to access
            // ;
            //good luck
           setWatchedCoins(data) //this makes sense.
        })
    }, [])
    useEffect(()=>{
        let newArray = [];
        console.log(watchedCoins)
        fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=${props.currency}&order=market_cap_desc&per_page=250&page=1&sparkline=falsekets`)
            .then(r=>r.json())
            .then(data=> {


                data.forEach(coin=>{
                    watchedCoins.forEach((watchedCoin) => { 
                        if(coin.id === watchedCoin.id){
                            newArray.push(coin);            // his scoping is weird. I'm not sure if this is going to return from the inner function in the forEach, the map, or the useEffect function. you can just use `find` for this btw. 
                        }
                    })
                })
                console.log(newArray)
            })
        setUpdatedWatchList(newArray)
        console.log(updatedWatchList)
    }, [watchedCoins.length > 0])



    useEffect(() => {
        return(
            <CryptoList coins={updatedWatchList} currency={props.currency} />
        )
    }, [updatedWatchList])

    return(
        <div className="WatchList">
            {updatedWatchList.length > 0 ? <CryptoList coins={updatedWatchList} currency={props.currency} /> : null}
        </div>
    )
}

export default WatchList;
