import React from "react";

function CryptoCoin({coin}){
    function currencyFormat(num) {
        if(num > .1){
            return '$' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
        }
        else{
            return '$' + num.toFixed(5);
        }
    }

    function percentageFormat(num){
        return num.toFixed(2) + "%";
    }



    return (
        <div className="CryptoCoin">
            <svg height="130" width="500">

                <rect width="1080" height="1080" fill="#55ceff" />
                <image href={coin.image} width="100" height="100" x="28" y="15"/>
                <text fill="lightgrey" fontSize="50" fontFamily="Marker Felt" x="150" y="60">{coin.name}</text>
                <text fill="#ffffff"  fontSize="25" fontFamily="Sans-serif" fontWeight="500" x="350" y="50">{currencyFormat(coin.current_price)}</text>
                <text fill="#ffffff"  fontSize="25" fontFamily="Sans-serif" fontWeight="500" x="350" y="250">{percentageFormat(coin.price_change_percentage_24h)}</text>
            </svg>
        </div>
    )
}

export default CryptoCoin;
