    // });
    if(coins.length > 0){
        const sortedCoins = coins.sort((a, b) => {
            if(a.current_price < b.current_price){
                return -1;
            }   
            if(a.current_price > b.current_price){
                return 1;
            } 
            else{
                return 0;
            }
       })
       .map((item, i) => <Crypto key={i} data={item} />);
       console.log(sortedCoins)
    }


