                <Dropdown.Item className="dropdown-item">Action</Dropdown.Item>
                <Dropdown.Item className="dropdown-item">Another action</Dropdown.Item>
                <Dropdown.Item className="dropdown-item">Something else</Dropdown.Item>
            </Dropdown.Menu>
        </Dropdown>
    )
}

export default Sort;
