import React from "react";

function CryptoCoin({coin}){
    function currencyFormat(num) {
        if(num > .1){
            return '$' + num.toFixed(2).replace(/(\d)(?=(\d{3})+(?!\d))/g, '$1,')
        }
        else{
            return '$' + num.toFixed(5);
        }
    }

    return (
        <div className="CryptoCoin">
            <svg height="130" width="500">
            <defs>

  </defs>
                <rect width="1080" height="1080" fill="orange" />
                <text fill="red" fontSize="50" fontFamily="Marker Felt" x="50" y="40">{coin.name}</text>
                <text fill="#ffffff" shadow="black" fontSize="25" fontFamily="Sans-serif" x="250" y="40">{currencyFormat(coin.current_price)}</text>
            </svg>
        </div>
    )
}

export default CryptoCoin;
