import React, {useState, useEffect} from 'react';
import CryptoList from './CryptoList';


function WatchList(props) {

    const [updatedWatchList, setUpdatedWatchList] = useState([]);

    let x;

    useEffect(() => {
        fetch("http://localhost:3000/watchList")
        .then(r=>r.json())
        .then(data=> {
            console.log(data)
            //console.log here to see what data looks like
            // check out key/value pairs of the object and find what you want to access
            // ;
            //good luck
           x = [data];
        })
    }, [])




    useEffect(()=>{
        console.log(x)
        fetch(`https://api.coingecko.com/api/v3/coins/markets?vs_currency=${props.currency}&order=market_cap_desc&per_page=250&page=1&sparkline=falsekets`)
            .then(r=>r.json())
            .then(data=> {
                setUpdatedWatchList(data.map(coin=>{
                    x.forEach((watchedCoin) => { 
                        if(coin.id === watchedCoin.id){
                            return coin;
                        }
                    })
                })) 
            })
    }, [x])

    return(
        <div className="WatchList">
            {updatedWatchList.length > 0 ? <CryptoList coins={updatedWatchList} currency={props.currency} /> : null}
        </div>
    )
}

export default WatchList;
